import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import PrivateRoute from './components/PrivateRoute';
import LoginPage from './pages/LoginPage';
import EmployeeList from './components/EmployeeList';
import EmployeeDetail from './components/EmployeeDetail';
import EmployeeForm from './components/EmployeeForm';
import './App.css'; // Import App.css here

function App() {
    return (
        <AuthProvider>
            <Router>
                <Switch>
                    <Route path="/login" component={LoginPage} />
                    <PrivateRoute path="/employees" exact component={EmployeeList} roles={['admin']} />
                    <PrivateRoute path="/employees/:id" component={EmployeeDetail} roles={['admin', 'employee']} />
                    <PrivateRoute path="/employees/new" component={EmployeeForm} roles={['admin']} />
                    <PrivateRoute path="/employees/edit/:id" component={EmployeeForm} roles={['admin']} />
                </Switch>
            </Router>
        </AuthProvider>
    );
}

export default App;
